var um_dislike_ajax = false;
jQuery( document ).ready( function() {
	jQuery(document.body).on('click', '.um-dislike-btn', function(e) {
		e.preventDefault();

		if ( um_dislike_ajax === true ) {
			return false;
		}
		um_dislike_ajax = true;

		var btn = jQuery(this);
		var member_directory = btn.parents( '.um-members-wrapper' ).length > 0;

		btn.addClass('um_dislike_ajax');
		var user_id = btn.data('user-id');

		wp.ajax.send( 'um_dislike', {
			data: {
				user_id: user_id,
				nonce: um_scripts.nonce
			},
			success: function( response ) {
				btn.replaceWith( response.btn );
				um_dislike_ajax = false;
			},
			error: function( e ) {
				console.log( e );
				um_dislike_ajax = false;
			}
		});
		return false;
	});
});